import{_ as i,o as l,c as e,f as a}from"./app-28f5d3d5.js";const r="/kindeeImg/kindee01.png",n="/kindeeImg/kindee02.png",t="/kindeeImg/kindee03.png",o={},d=a('<h2 id="金蝶云苍穹" tabindex="-1"><a class="header-anchor" href="#金蝶云苍穹" aria-hidden="true">#</a> 金蝶云苍穹</h2><p><strong>动态微服务架构：</strong></p><p><img src="'+r+'" alt="image-20230104114203266"></p><p><strong>无状态服务的实现：</strong></p><p><img src="'+n+'" alt="image-20230104131913822"></p><p>高性能架构——多级<strong>缓存服务：</strong></p><p><img src="'+t+'" alt="image-20230104132025012"></p><h4 id="开发概述" tabindex="-1"><a class="header-anchor" href="#开发概述" aria-hidden="true">#</a> 开发概述</h4><ul><li>开发环境版本</li><li>环境搭建</li><li>开发产物及管理</li><li>补丁制作与升级</li><li>迭代流程</li><li>学习资源及渠道</li></ul><h3 id="云和应用设计" tabindex="-1"><a class="header-anchor" href="#云和应用设计" aria-hidden="true">#</a> 云和应用设计</h3><ol><li>业务划分</li><li>微服务划分</li><li>数据库分库</li><li>项目工程设计</li></ol><h3 id="云-的定义" tabindex="-1"><a class="header-anchor" href="#云-的定义" aria-hidden="true">#</a> “云”的定义</h3><ol><li>一个完整的业务领域解决方案</li><li>一个业务云中可包括多个应用</li><li>一个业务云与其他业务云可有业务相关性</li><li>通常一个业务云的数据存放在同一个数据库，也支持将结构业务云数据存放在一个数据库</li></ol><h3 id="应用-的定义" tabindex="-1"><a class="header-anchor" href="#应用-的定义" aria-hidden="true">#</a> “应用”的定义</h3><ol><li>一个可独立使用的业务、或者是一组具有相同意义的功能</li><li>在企业应用迭代时，可以选择一个业务云中的不同应用进行实施、升级和迭代</li><li><strong>微服务的最小粒度是应用（高内聚、低耦合）</strong></li><li>应用划分应尽量避免跨应用的写操作</li><li>应用是整个业务云进行分类管理的根本（页面、功能）</li></ol><h3 id="总结" tabindex="-1"><a class="header-anchor" href="#总结" aria-hidden="true">#</a> 总结</h3><ol><li>“云”和“应用”设计决定了系统的业务划分</li><li>“应用”的划分除了通用标准外，防止业务跨库写是重要标准</li><li>一个“应用”就是一个微服务</li><li>一个&quot;应用&quot;可以独立部署在一个容器中，也可以多个“应用”部署在一个容器中</li><li>分库推荐按”云“做垂直分库，一个”云“中所有”应用“都指定到同一个库中</li><li>只有“应用”的业务完全独立，或数据量、并发量很大时才可以按“应用”分库</li></ol><h3 id="页面建模" tabindex="-1"><a class="header-anchor" href="#页面建模" aria-hidden="true">#</a> 页面建模</h3><p><strong>通用列表的设计</strong></p><ol><li>单据列表控件属性：分页条数、缺省视图、已选合计字段；</li><li>表格/卡片视图属性：分页隐藏、界面规则、折叠显示、折叠表单、工具栏位置</li><li>、表格列表字段属性：排序、汇总方式</li><li>列表控制在运行态，列表的左上角调整，可设置隐藏/冻结指定列，隐藏序号列、数值列尾零显示、禁止操作后刷新列表数据、同一单据拆分显示、交替行显示、未勾选时引出列表全部数据</li></ol><p><strong>动态表单的设计</strong></p><ol><li>动态表单是苍穹平台中最基本的页面类型</li><li>数据存储和查询都需要通过插件自行完成</li><li>动态表单的业务属性可参考单据上相应的属性</li></ol><h5 id="移动界面开发" tabindex="-1"><a class="header-anchor" href="#移动界面开发" aria-hidden="true">#</a> 移动界面开发</h5><ol><li>移动端对于控件的使用与PC端大致相同</li><li>善用移动端页面布局中的场景卡片等平台提供的块状模板，可以快速实现页面布局</li><li>在设计移动端页面时候善于使用Flex面板，卡片界面善于使用卡片布局容器这样可以针对不同模块做不同的样式调整</li><li>移动端的多数逻辑要通过插件实现，内置的方法使用与PC端基本一致，如果想要其他扩展功能可以去实现对应接口方法。</li></ol>',24),h=[d];function s(c,p){return l(),e("div",null,h)}const _=i(o,[["render",s],["__file","kindee.html.vue"]]);export{_ as default};
